import { INPUT_CHANGE } from '../actions/types';

const initialState = {
    inputValue: ''
}

export default function(state = initialState, action){
    switch(action.type){
        case INPUT_CHANGE:
            return {
                ...state,
                inputValue: action.text
            };
            default: 
            return state;
    }
}
