import React from 'react';
import './SearchBar.css'
import { connect } from 'react-redux';




class SearchBar extends React.Component {
    constructor(props){
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentWillMount(){

    }

    handleChange(e){
           this.setState({term: e.target.value});
    }

    handleSubmit(e){
        e.preventDefault();
        const cheese = 'cheese';
        fetch(`http://hn.algolia.com/api/v1/search?query=${cheese}`, {method: 'GET'}, {headers: { 'content-type': 'applications/json'}}).then(res => res.json()).then(data => console.log(data));
    }

    render(){
            return <div className="form">
                <form onSubmit={this.handleSubmit}>
                    <input onChange={this.props.inputChange} type="text"></input>
                    <button className="button" type="submit">search</button>
                </form>
                    </div>
    }
}

const mapDispatchToProps = dispatch => {
return {
    inputChange: (evt) => {
        console.log('changed', evt);
        const action = {type: 'INPUT_CHANGE', text: ''};
        store.dispatch(action);
    }
}
}



export default connect(mapDispatchToProps)(SearchBar);
