import { FETCH_GET, FETCH_NEW } from '../actions/types';

const initialState = {
    items: [],
    term: '',
    item: {}
}

export default function(state = initialState, action){
    switch(action.type){
        case FETCH_GET:
            return {
                ...state,
                items: action.payload
            }
            case NEW_TERM:
                return {
                    ...state,
                    term: action.term
                }
            default:
                return state;
    }

}
