import React from 'react';
import './SearchBar.css'
import { connect } from 'react-redux';





class SearchBar extends React.Component {
    constructor(props){
        super(props);

        this.handleSubmit = this.handleSubmit.bind(this);
    }




    handleSubmit(e){
        e.preventDefault();
        let term = this.props.inputValue;
        console.log(term);
      fetch(`https://cors-anywhere.herokuapp.com/https://hn.algolia.com/api/v1/search?query=${term}`, { method: 'GET'}).then(res => res.json()).then(data => console.log(data));
    }

    render(){
            return <><div className="form">
                <form onSubmit={this.handleSubmit}>
                    <input value={this.props.inputValue} onChange={this.props.inputChange} type="text"></input>
                    <button className="button" type="submit">search</button>
                </form>
                    </div>
                    <div>{this.props.inputValue}</div>
                    </>

    }
}

const mapStateToProps = state => {
return {
    inputValue: state.inputValue
}
}

const mapDispatchToProps = dispatch => {
return {
    inputChange: (e) => {
        console.log('changed', e.target.value);
        const action = {type: 'INPUT_CHANGE', text: e.target.value};
       dispatch(action);
    }
}
}



export default connect(mapStateToProps,mapDispatchToProps)(SearchBar);
