import React from 'react';


class SearchResults extends React.Component{
  constructor(props){
      super(props);
      this.state = {
          searchResults: []
      }
  }

    componentDidMount(){
        fetch('http://hn.algolia.com/api/v1/items/1', {method: 'GET'},)
        .then(res => res.json())
        .then(data => this.setState({searchResults: data.children}));
    }
    render(){    
       let postResults = this.state.searchResults;
       postResults = postResults.map(result => {
           return <div key={result.id}>
               <span>{result.author}</span>
               <span>{result.text}</span>
           </div>
       })

        return <div><h1>List</h1>{postResults}</div>
    }
}


export default SearchResults;
