import { FETCH_GET, FETCH_NEW } from '../actions/types';

const initialState = {
    items: [],
    item: {}
}

export default function(state = initialState, action){
    switch(action.type){
        case FETCH_GET:
            return {
                ...state,
                items: action.results
            }
    }

}
