import { INPUT_CHANGE } from '../actions/types';

const initialState = {
    inputValue: ''
}

export default function(state = initialState, action){
    switch(action.type){
        case INPUT_CHANGE:
            console.log('nigra', action);
            return Object.assign({}, state, {inputValue: action.text});
            default: 
            return state;
    }
}
