import React from 'react';
import './SearchResults.css';
import {connect} from 'react-redux';
import {fetchResults} from '../../actions/resultsActions';

class SearchResults extends React.Component{

componentWillMount(){
    this.props.fetchResults();
}

    render(){   

      const resultItems = this.props.results.map(result => (
         <div key={result.id}><span>{result.author}</span>
         <span>{result.text}</span></div>
      ));    
    return (
        <div>
            <h1>List</h1>
            {resultItems}
        </div>
    )
    }
}

const mapStateToProps = state => ({
    results: state.results.items
});

export default connect(mapStateToProps,{fetchResults} )(SearchResults);
